import React, { useState } from "react";
import { Text, StyleSheet, View, Image, TextInput, Keyboard, TouchableOpacity } from "react-native";

const Goals = () => {
  const [items, setItems] = useState([]);

  return (
    <View>
      <Text style={styles.sectionHeader}>Purchase Goals</Text>
      {items.length == 0 && <Text>Saving up for an item? Add it here!</Text>}

      <View style={styles.inputContainer}>
        <View style={styles.inputField}>
          <Text style={styles.preInputField}>$</Text>
          <TextInput
            style={styles.textInput}
            placeholder="0.00"
            maxLength={20}
            onBlur={Keyboard.dismiss}
            onChangeText={(change) => setTransactionVal(change)}
          />
        </View>
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.addButton} onPress={addTransaction}>
            <Text style={styles.buttonText}>Add</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.buttonContainer}>
          <TouchableOpacity
            style={styles.subtractButton}
            onPress={subtractTransaction}
          >
            <Text style={styles.buttonText}>Subtract</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  sectionHeader: {
    flex: 1,
    flexDirection: "column",
    justifyContent: "space-between",
    fontSize: 20,
    fontWeight: "bold",
    paddingBottom: "5%",
    paddingTop: "10%",
    textAlign: "left",
    color: "#373737",
  },

  // for input area
  inputContainer: {
    paddingTop: 15,
    flex: 1,
    flexDirection: "row",
  },
  preInputField: {
    color: "#CCCCCC",
    fontSize: 20,
    paddingRight: "3%"
  },
  inputField: {
    paddingTop: 15,
    flex: 2,
    flexDirection: "row",
  },
  textInput: {
    borderColor: "#CCCCCC",
    borderWidth: 1,
    height: "100%",
    width: "80%",
    fontSize: 20,
    paddingLeft: "10%",
    paddingRight: "10%",
  },
});

export default Goals;
